import React, { useState } from 'react'

interface Props {
  content?: string;
}

const regExp = RegExp(/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/)
const API_HOST = 'http://localhost:4000'
const INVENTORY_API_URL = `${API_HOST}/inventory`

const UserForm: React.FC<Props> = ({}): React.ReactElement => {
  const INITIAL_STATE = {
    name: '',
    email: '',
    phoneNumer: ''
  }
  const [isError, setIsError] = useState(INITIAL_STATE)
  const [inputValue, setInputValue] = useState({})

  const [valid, setValid] = useState(true)

  const formValid = (): void => {
    Object.values(isError).forEach((val) => {
      if (val.length > 0) {
        setValid(false)
      } else {
        setValid(true)
      }
    })
  }

  const onSubmit = (e: any): void => {
    e.preventDefault()
    formValid()
    if (valid) {
      // eslint-disable-next-line no-console
      console.log('value', inputValue)
    } else {
      // eslint-disable-next-line no-console
      console.log('Form is invalid!')
    }
  }

  const formValChange = (e: any): void => {
    e.preventDefault()
    const { name, value } = e.target

    switch (name) {
      case 'name':
        const nameError =
          value.length < 4 ? 'Atleast 4 characaters required' : ''
        setIsError({
          ...isError,
          name: nameError
        })
        break
      case 'email':
        const emailError = regExp.test(value) ? '' : 'Email address is invalid'
        setIsError({
          ...isError,
          email: emailError
        })
        break
      case 'phoneNumer':
        const phoneNumerError =
          value.length < 6 ? 'Atleast 6 characaters required' : ''
        setIsError({
          ...isError,
          phoneNumer: phoneNumerError
        })
        break
      default:
        break
    }
    setInputValue({
      ...inputValue,
      [name]: value
    })
  }
  // eslint-disable-next-line no-console
  console.log('inputValue', inputValue)

  const updateInventory = ({  }): void => {
    // eslint-disable-next-line no-console
