import React, { useState } from 'react'
import UserForm from '../UserForm/UserForm'

interface Props {
  content?: string;
}
const Initial_Data =  [
  {
    'id': 1,
    'product_name': 'Weetabix',
    'product_category': 'Cereal',
    'unit_price': '501'
  },
  {
    'id': 2,
    'product_name': 'Colgate Toothpaste',
    'product_category': 'Toiletries',
    'unit_price': '119'
  },
  {
    'id': 3,
    'product_name': 'Imperial Leather Soap',
    'product_category': 'Toiletries',
    'unit_price': '235'
  },
  {
    'id': 4,
    'product_name': 'Sunlight Detergent',
    'product_category': 'Toiletries',
    'unit_price': '401'
  }
]

const UserTable: React.FC<Props> = ({}): React.ReactElement => {
  const [data, setData] = useState(Initial_Data)

  const [inEditMode, setInEditMode] = useState({
    status: false,
    rowKey: null
});

const [unitPrice, setUnitPrice] = useState(null)

   const onEdit = ({id, currentUnitPrice}) => {
    setInEditMode({
        status: true,
        rowKey: id
    })
    setUnitPrice(currentUnitPrice);
}

const onSave = ({id, newUnitPrice}) => {
   // eslint-disable-next-line no-console
   console.log("")
}

const onCancel = () => {
    // reset the inEditMode state value
    setInEditMode({
        status: false,
        rowKey: null
    })
    // reset the unit price state value
    setUnitPrice(null);
}

  return (
    <div className='container'>
      <h1>Simple Inventory Table</h1>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Product Category</th>
            <th>Unit Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {
            data.map((item) => (
              <tr key={item.id}>
                <td>{item.product_name}</td>
                <td>{item.product_category}</td>
                <td>{item.unit_price}</td>
                <td/>
              </tr>
            ))
          }
        </tbody>
      </table>
    </div>
  )
}

export default UserTable
